function rechercherProducteurPourProduits(motsCles, idTable, idBouton) {

  $.ajax({
    url: '/src/app/controller/ajaxhandlers/cet.annuaire.controller.ajaxhandler.producteurs.produit.nom.php?prds=' + motsCles,
    success: function(json) {

      var producteurs = JSON.parse(json);
      if (producteurs.err == undefined) {
        count = 0;
        for (var i = 0; i < producteurs.length; ++i) {
          adrComplete = producteurs[i].adrferme_numvoie + ' ' + producteurs[i].adrferme_rue 
            + ' ' + producteurs[i].adrferme_lieudit 
            + ' ' + producteurs[i].adrferme_commune 
            + ' ' + producteurs[i].adrferme_cp 
            + ' ' + producteurs[i].adrferme_compladr;
          trow = '<tr><td><a href="./?statut=fichedetailleeprd&anr=true&pkprd=' + producteurs[i].pk_producteur 
            +'" target="_blank">' + producteurs[i].nom_ferme + '</a></td><td>' + adrComplete + '</td></tr>';
          $('#' + idTable).append(trow);
          ++count;
        }
        $('#' + idTable).prepend('<tr><td colspan="2">' + count + ' Producteur.e.s trouvés : </td></tr>');

      } else {
         $('#' + idTable).html(producteurs.err);
      }

      $('#' + idBouton).hide();
    },
    error: function(jqXHR, textStatus, errorThrown) {
      $('#' + idTable).html('Aucun producteur trouvé.');
      $('#' + idBouton).hide();
    }
  });

}