$(document).ready(function() {
	$('#cet-annuaire-recherche-filtrer').on('mousedown', function(e) {
		console.log('val=' + $('#cet-annuaire-recherche-filtre').val());
    this.href += $('#cet-annuaire-recherche-filtre').val();
  });

    var oldUrl =  $('#cet-annuaire-button-filter').attr("href");
    $('#cet-annuaire-select-filter').change(function(e) {
        var attribute = $('#cet-annuaire-select-filter').val();
        var currentUrl = oldUrl + attribute;
        $('#cet-annuaire-button-filter').attr("href",currentUrl);
    });
});